import React, { useState } from "react";
//import { useNavigate } from "react-router";
import "bootstrap/dist/css/bootstrap.css";
import { useNavigate, Link } from "react-router-dom"
import './style.css';

export default function Login() {
 const [form, setForm] = useState({
   email: "",
   password: "",
 });
 const navigate = useNavigate();

 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }


// This function will handle the submission.
async function onSubmit(e) {
  e.preventDefault();

  // When a post request is sent to the create url, we'll add a new record to the database.
  const loginCredentials = { ...form };

  try {
    const response = await fetch("http://localhost:5050/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginCredentials),
    });

    const data = await response.text(); // Assuming server sends back a plain text message

    if (response.ok) {
      // If the login was successful, redirect to the app page
      navigate("/app/");
    } else {
      // If the login was unsuccessful, alert the user
      window.alert(data); // Data contains the response message from the server
    }
  } catch (error) {
    window.alert("There was a problem with the login request: " + error.message);
  }

  // Reset the form
  setForm({ email: "", password: "" });
}


 // This following section will display the form that takes the input from the user.
 return (
   <div className="container text-left">
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
     <h1>Login</h1>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="email">Email</label>
         <input
           type="text"
           className="form-control"
           id="email"
           value={form.email}
           onChange={(e) => updateForm({ email: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="password">Password</label>
         <input
           type="password"
           className="form-control"
           id="password"
           value={form.password}
           onChange={(e) => updateForm({ password: e.target.value })}
         />
       </div>
       <div className="form-group">
         <input
           type="submit"
           value="Login"
           className="btn btn-submit"
         />
       </div>
     </form>

      {/* Add a link to the registration page */}
      <p>
        Don't have an account? <Link to="/register">Register here</Link>
      </p>

   </div>



 );
}
